INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('41bbe30f-097a-4404-89ac-a28c30335be5',
        'Геносоциограмма позволяет получить наглядное социометрическое (аффективное) представление о генеалогическом семейном древе с его характеристиками фамилий, имен, мест, дат, связей и главных событий жизни: рождений, бракосочетаний, кончин, значимых болезней, несчастных случаев, переездов, выборов профессий, выходов на пенсию. Геносоциограмма является комментированным представлением генеалогического древа (генограммы). На ней с помощью социометрических стрелок выделены различные типы отношений субъекта со своим окружением и связи между различными персонажами; соприсутствие, сожительство, содействие, диады, треугольники, исключения — «кто с кем живет под одной крышей» и «ест из одного котла», кто чьих детей растит, кто и куда убегает, кто прибавляется (рождается, приезжает) в тот момент, когда другой уходит (умирает или уезжает), кто кого заменяет в семье и как происходит раздел... особенно после смерти (наследства, подарков), одаренные, обделенные, «несправедливости» (семейные и социальные счета), повторения...',
        'Геносоциограмма позволяет получить наглядное социометрическое (аффективное) представление о генеалогическом семейном древе с его характеристиками фамилий, имен, мест, дат, связей и главных событий жизни: рождений, бракосочетаний, кончин, значимых болезней, несчастных случаев, переездов, выборов профессий, выходов на пенсию. Геносоциограмма является комментированным представлением генеалогического древа (генограммы). На ней с помощью социометрических стрелок выделены различные типы отношений субъекта со своим окружением и связи между различными персонажами; соприсутствие, сожительство, содействие, диады, треугольники, исключения — «кто с кем живет под одной крышей» и «ест из одного котла», кто чьих детей растит, кто и куда убегает, кто прибавляется (рождается, приезжает) в тот момент, когда другой уходит (умирает или уезжает), кто кого заменяет в семье и как происходит раздел... особенно после смерти (наследства, подарков), одаренные, обделенные, «несправедливости» (семейные и социальные счета), повторения...',
        'Анн Анселин Шутценбергер',
        'Синдром предков. Трансгенерационные связи, семейные тайны, синдром годовщины, передача травм и практическое использование геносоциограммы',
        null, -212874107, '2023-11-11 07:47:59.481856', '2023-11-11 07:47:59.481859', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('3ea5e947-9603-4f41-9740-4326e0a749f8',
        'Поскольку проблема экзистенциальной изоляции неразрешима, терапевт должен развенчивать ее иллюзорные решения. Попытки человека избежать изоляции могут препятствовать нормальным отношениям с другими людьми. Многие дружбы и браки распадаются потому, что вместо проявления заботы друг о друге партнеры используют друг друга как средство борьбы со своей изоляцией. Довольно распространенная и действенная попытка избежать экзистенциальной изоляции, встречающаяся в нескольких моих историях, – это слияние, размывание границ собственной личности, растворение в другом. Сила тенденции к слиянию была продемонстрирована экспериментом с подпороговым восприятием, в котором фраза «Мы с мамой одно целое» мелькала на экране так быстро, что испытуемые не могли сознательно воспринимать ее, но сообщали о том, что они почувствовали себя лучше, сильнее, увереннее. Эффект проявлялся даже в сравнительном улучшении результатов терапии (включающей изменение поведения) курения, ожирения и подростковых поведенческих проблем. Один из величайших жизненных парадоксов заключается в том, что развитие самосознания усиливает тревогу. Слияние рассеивает тревогу самым радикальным образом – уничтожая самосознание. Человек, который влюбляется и переживает блаженное состояние слияния с любимым, не рефлексирует, поскольку его одинокое сомневающееся «Я» и сопутствующий страх изоляции растворяются в «мы». Таким образом, человек избавляется от тревоги, теряя самого себя. Вот почему терапевты не любят иметь дело с влюбленными пациентами. Терапия и любовное слияние несовместимы, поскольку для терапевтической работы необходимо сомневающееся «Я» и тревога, которая служит указанием на внутренние конфликты.',
