        'Мари Кардиналь', 'Слова, которые исцеляют', null, -1800088619, '2023-11-11 07:48:10.718026',
        '2023-11-11 07:48:10.718034', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
