INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('c9377711-93cd-4009-8ff6-6e6228bd6829',
        '«Немаловажную роль играют, как я думаю, — говорит Вундт, — в сновидениях субъективные зрительные и слуховые ощущения, знакомые нам в бодрственном состоянии в форме смутного ощущения света при закрытых глазах, шума и звона в ушах и так далее, особенно же субъективные раздражения сетчатой оболочки. Этим и объясняется изумительная склонность сновидения вызывать перед взглядом спящего множество аналогичных или вполне совпадающих между собою объектов. Мы видим перед собою бесчисленных птиц, бабочек, рыб, пестрые камни, цветы и т. п. Световая пыль темного круга зрения принимает фантастические формы, а многочисленные световые точки, из которых состоит она, воплощаются сновидением в столь же многочисленные предметы, которые вследствие подвижности светового хаоса кажутся движущимися вещами. Здесь коренится также сильная склонность сновидения к самым разнообразным фигурам животных, богатство форм которых легко приноравливается к особой форме субъективных световых картин».',
        '«Немаловажную роль играют, как я думаю, — говорит Вундт, — в сновидениях субъективные зрительные и слуховые ощущения, знакомые нам в бодрственном состоянии в форме смутного ощущения света при закрытых глазах, шума и звона в ушах и так далее, особенно же субъективные раздражения сетчатой оболочки. Этим и объясняется изумительная склонность сновидения вызывать перед взглядом спящего множество аналогичных или вполне совпадающих между собою объектов. Мы видим перед собою бесчисленных птиц, бабочек, рыб, пестрые камни, цветы и т. п. Световая пыль темного круга зрения принимает фантастические формы, а многочисленные световые точки, из которых состоит она, воплощаются сновидением в столь же многочисленные предметы, которые вследствие подвижности светового хаоса кажутся движущимися вещами. Здесь коренится также сильная склонность сновидения к самым разнообразным фигурам животных, богатство форм которых легко приноравливается к особой форме субъективных световых картин».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 810920416, '2023-11-11 07:48:10.785295',
        '2023-11-11 07:48:10.785297', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('a54aadbf-960d-4b37-867c-3500df892085',
        'Если спросить интеллигентного человека, в общем незнакомого с литературой вопроса, как образуется сновидение, он несомненно ответит, сославшись на какой-нибудь известный ему сон, что сновидение объясняется объективным чувственным раздражением, испытанным при пробуждении. Научное исследование не может остановиться, однако, на этом. Повод к дальнейшим вопросам оно черпает из того наблюдения, что раздражение, действующее на органы чувств во время сна, проявляется в сновидении не в своем действительном виде, а заменяется каким-либо другим представлением, находящимся с ним в каком-либо отношении. Отношением этим, связующим раздражение с окончанием сна, по словам Мори, является «любая связь, но которая не является ни единственной, ни исключительной».',
        'Если спросить интеллигентного человека, в общем незнакомого с литературой вопроса, как образуется сновидение, он несомненно ответит, сославшись на какой-нибудь известный ему сон, что сновидение объясняется объективным чувственным раздражением, испытанным при пробуждении. Научное исследование не может остановиться, однако, на этом. Повод к дальнейшим вопросам оно черпает из того наблюдения, что раздражение, действующее на органы чувств во время сна, проявляется в сновидении не в своем действительном виде, а заменяется каким-либо другим представлением, находящимся с ним в каком-либо отношении. Отношением этим, связующим раздражение с окончанием сна, по словам Мори, является «любая связь, но которая не является ни единственной, ни исключительной».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, -72534084, '2023-11-11 07:48:10.764845',
        '2023-11-11 07:48:10.764846', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('341ca451-bf0a-4c89-af2a-0118c3cff8a0',
        'Ощущение, согласно какому-либо закону ассоциации, вызывает родственное ему представление и вместе с тем соединяется в одно органическое целое, на которое, однако, сознание реагирует иначе, нежели в нормальном состоянии. Оно обращает внимание не на само ощущение, а только на сопутствующие представления, что служит одновременно и причиной того, почему такое положение вещей до сих пор не было подмечено. Краусс называет этот процесс особым термином – «транссубстанцией» ощущений в сновидениях.',
        'Ощущение, согласно какому-либо закону ассоциации, вызывает родственное ему представление и вместе с тем соединяется в одно органическое целое, на которое, однако, сознание реагирует иначе, нежели в нормальном состоянии. Оно обращает внимание не на само ощущение, а только на сопутствующие представления, что служит одновременно и причиной того, почему такое положение вещей до сих пор не было подмечено. Краусс называет этот процесс особым термином – «транссубстанцией» ощущений в сновидениях.',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 1986990421, '2023-11-11 07:48:10.776927',
        '2023-11-11 07:48:10.776929', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
