INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('523683b4-3b4f-419a-8def-164093336e27',
        '«Самое удивительное то, что сновидение обычно заимствует свои элементы не из крупных и существенных факторов, не из важных и побудительных интересов прошедшего дня, а из второстепенных явлений, так сказать, из ничтожных обломков недавнего пережитого или же, наоборот, далекого прошлого. Потрясающий случай смерти в нашей семье, под впечатлением которого мы засыпаем, как бы погашается в нашей памяти, пока первый момент бодрствования не возвращает его в наше сознание с удвоенною силою. Напротив того, бородавка на лбу встреченного нами незнакомца, о котором мы забыли, тотчас же, как только прошли мимо него, – играет в нашем сновидении наиболее видную роль...».',
