        'Зигмунд Фрейд', 'Толкование сновидений', null, -1720724907, '2023-11-11 07:48:12.699990',
        '2023-11-11 07:48:12.699991', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
