INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('f896b44a-51c6-40a4-b750-a49682591305',
        'Ни из одного исследования человеческого поведения не следует логический вывод о том, какое поведение "правильное". Если большинство людей не делает чего-то, из этого вовсе не следует, что их поведение "неправильное". И наоборот, если большинство что-то делает, это совсем не значит, что подобное поведение правильно.',
        'Ни из одного исследования человеческого поведения [...] не следует логический вывод о том, какое поведение "правильное". Если большинство людей не делает чего-то, из этого вовсе не следует, что их поведение "неправильное". И наоборот, если большинство что-то делает, это совсем не значит, что подобное поведение правильно.',
        'Дэвид Майерс', 'Социальная психология', null, -2143596235, '2023-11-11 07:46:33.510160',
        '2023-11-11 07:46:33.510165', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('66ea86ee-0570-46d9-9e1c-fb5eb7bc07a8',
        '«...жизнь дня с ее треволнениями и переживаниями, с радостями и горестями никогда не воспроизводится в сновидении; последнее стремится скорее вырвать нас из этой жизни. Даже когда вся наша душа преисполнена одной мыслью, когда острая боль разрывает наше сердце или когда какая-либо цель поглощает целиком наш разум, – даже тогда сновидение оживляет нечто совершенно своеобразное, или же берет для своих комбинаций только отдельные элементы действительности, или же, наконец, входит в тон нашего настроения и символизирует действительность».',
        '«...жизнь дня с ее треволнениями и переживаниями, с радостями и горестями никогда не воспроизводится в сновидении; последнее стремится скорее вырвать нас из этой жизни. Даже когда вся наша душа преисполнена одной мыслью, когда острая боль разрывает наше сердце или когда какая-либо цель поглощает целиком наш разум, – даже тогда сновидение оживляет нечто совершенно своеобразное, или же берет для своих комбинаций только отдельные элементы действительности, или же, наконец, входит в тон нашего настроения и символизирует действительность».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 1248374236, '2023-11-11 07:48:12.804571',
        '2023-11-11 07:48:12.804577', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('e3cffa70-638d-40f3-95bc-277c5709a798',
        '«Еще более странно, когда замечаешь, как сновидение черпает в полной неприкосновенности, в первоначальной свежести образы отдельных лиц, вещей и местностей из глубочайших наслоений, отложенных временем на ранних переживаниях юности. Это ограничивает не только впечатлениями, вызвавшими при своем возникновении живое сознание или связанными с высокими психическими ценностями и возвращающимися впоследствии в сновидении в качестве воспоминания, которому радуется пробудившееся сознание. Глубина памяти в сновидении обнимает собою также и те образы, вещи, лица, местности и переживания раннего периода, которые либо вызвали лишь незначительное сознание, либо не обладали никакой психической ценностью, либо же утратили как то, так и другое. Поэтому как в сновидении, так и по пробуждении они представляются совершенно новыми и незнакомыми – до тех пор пока не открывается их раннее происхождение».',
        '«Еще более странно, когда замечаешь, как сновидение черпает в полной неприкосновенности, в первоначальной свежести образы отдельных лиц, вещей и местностей из глубочайших наслоений, отложенных временем на ранних переживаниях юности. Это ограничивает не только впечатлениями, вызвавшими при своем возникновении живое сознание или связанными с высокими психическими ценностями и возвращающимися впоследствии в сновидении в качестве воспоминания, которому радуется пробудившееся сознание. Глубина памяти в сновидении обнимает собою также и те образы, вещи, лица, местности и переживания раннего периода, которые либо вызвали лишь незначительное сознание, либо не обладали никакой психической ценностью, либо же утратили как то, так и другое. Поэтому как в сновидении, так и по пробуждении они представляются совершенно новыми и незнакомыми – до тех пор пока не открывается их раннее происхождение».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 322175583, '2023-11-11 07:48:12.720097',
        '2023-11-11 07:48:12.720099', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('fe44612a-9bde-403e-9071-b08bef192a4d',
        'Прежде всего бросается в глаза то, что в содержании сновидения имеется материал, за которым по пробуждении человек отрицает принадлежность к своему кругу знаний и переживаний. Он вспоминает, что это снилось ему, но не помнит, что когда-либо пережил это. Он остается затем в неизвестности, из какого источника черпало сновидение; им овладевает искушение уверовать в самостоятельную творческую способность сновидения; но неожиданно, спустя долгое время, новое переживание переносит мнимо утерянное воспоминание на более раннее переживание и находит тем самым источник сновидения. Приходится сознаваться тогда, что в сновидении человек знал и вспомнил нечто, чего не помнил в бодрственном состоянии (Вашид утверждает также, что неоднократно было замечено, что в сновидении человек говорит свободнее и лучше на иностранном языке, чем в бодрственном состоянии).',
        'Прежде всего бросается в глаза то, что в содержании сновидения имеется материал, за которым по пробуждении человек отрицает принадлежность к своему кругу знаний и переживаний. Он вспоминает, что это снилось ему, но не помнит, что когда-либо пережил это. Он остается затем в неизвестности, из какого источника черпало сновидение; им овладевает искушение уверовать в самостоятельную творческую способность сновидения; но неожиданно, спустя долгое время, новое переживание переносит мнимо утерянное воспоминание на более раннее переживание и находит тем самым источник сновидения. Приходится сознаваться тогда, что в сновидении человек знал и вспомнил нечто, чего не помнил в бодрственном состоянии (Вашид утверждает также, что неоднократно было замечено, что в сновидении человек говорит свободнее и лучше на иностранном языке, чем в бодрственном состоянии).',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 355041174, '2023-11-11 07:48:12.736669',
        '2023-11-11 07:48:12.736684', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('0890e819-cad5-4b39-8e87-240722792d8b',
        'Если человек реагирует мстительностью на то, что показалось ему неуважением, в картине преобладает раненая гордость. Если в результате той же провокации он становится жалким и пытается снискать расположение - это наиболее ясное проявление презрения к себе.',
        'Если человек реагирует мстительностью на то, что показалось ему неуважением, в картине преобладает раненая гордость. Если в результате той же провокации он становится жалким и пытается снискать расположение - это наиболее ясное проявление презрения к себе.',
        'Карен Хорни', 'Невроз и рост личности', null, -603652253, '2023-11-11 07:48:12.810875',
        '2023-11-11 07:48:12.810878', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('0fc14304-f981-4366-9e6f-8c142e8ee610',
        '«А все же, – продолжает Гилъдебрандт, – мнимое противоречие вполне правдиво и правильно. На мой взгляд, эта замкнутость и обособленность идет рука об руку с наитеснейшей связью и общностью. Мы можем сказать даже: что бы ни представляло собою сновидение, оно берет свой материал из действительности и из душевной жизни, разыгрывающейся на фоне этой действительности...Что бы оно ни делало с ним, оно никогда не отделится от реального мира и его самые комичные и странные формы должны будут всегда черпать свой материал из того, что либо стояло перед нашими глазами в действительной жизни или же уже заняло так или иначе место в нашем бодрственном мышлении, короче говоря, из того, что мы переживали внешне или внутренне».',
        '«А все же, – продолжает Гилъдебрандт, – мнимое противоречие вполне правдиво и правильно. На мой взгляд, эта замкнутость и обособленность идет рука об руку с наитеснейшей связью и общностью. Мы можем сказать даже: что бы ни представляло собою сновидение, оно берет свой материал из действительности и из душевной жизни, разыгрывающейся на фоне этой действительности...Что бы оно ни делало с ним, оно никогда не отделится от реального мира и его самые комичные и странные формы должны будут всегда черпать свой материал из того, что либо стояло перед нашими глазами в действительной жизни или же уже заняло так или иначе место в нашем бодрственном мышлении, короче говоря, из того, что мы переживали внешне или внутренне».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, 1817283705, '2023-11-11 07:48:12.750666',
        '2023-11-11 07:48:12.750677', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('d14cf217-0031-4235-8cbc-83929086b68b',
        'Все сновидение представляется в виде какого-то чуждого феномена, проявившегося между двумя вполне подходящими друг другу и составляющими один продолжение другого периодами (бодрственной) жизни.',
        'Все сновидение представляется в виде какого-то чуждого феномена, проявившегося между двумя вполне подходящими друг другу и составляющими один продолжение другого периодами (бодрственной) жизни.',
        'Зигмунд Фрейд', 'Толкование сновидений', null, -302994545, '2023-11-11 07:48:12.757415',
        '2023-11-11 07:48:12.757420', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('16d93403-18f9-48dd-b196-2804e2332c6c',
        '«Первый из этих контрастов образует, с одной стороны, полная отделенность, или замкнутость, сновидения от действительной, реальной жизни и, с другой стороны, постоянное соприкосновение их друг с другом, постоянная их взаимозависимость. Сновидение есть нечто строго отделенное от действительности, пережитой в бодрственном состоянии, так сказать, герметически замкнутое бытие, отрезанное от действительной жизни непроходимой пропастью. Оно отрывает нас от действительности, убивает в нас нормальное воспоминание о ней, переносит нас в другой мир, в другую среду, не имевшую решительно ничего общего с действительностью...».',
        '«Первый из этих контрастов образует, с одной стороны, полная отделенность, или замкнутость, сновидения от действительной, реальной жизни и, с другой стороны, постоянное соприкосновение их друг с другом, постоянная их взаимозависимость. Сновидение есть нечто строго отделенное от действительности, пережитой в бодрственном состоянии, так сказать, герметически замкнутое бытие, отрезанное от действительной жизни непроходимой пропастью. Оно отрывает нас от действительности, убивает в нас нормальное воспоминание о ней, переносит нас в другой мир, в другую среду, не имевшую решительно ничего общего с действительностью...».',
        'Зигмунд Фрейд', 'Толкование сновидений', null, -2064022817, '2023-11-11 07:48:12.761531',
        '2023-11-11 07:48:12.761536', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
