INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('1fdb0cdb-361b-4824-bc5b-652af96099b5',
        'Как бы то ни было, Юнг дополняет работы Фрейда выявлением синхронии* и того, что он называет «коллективным бессознательным». Согласно Юнгу, это «работающее» в нас коллективное бессознательное, передаваемое в обществе от поколения к поколению, аккумулирует человеческий опыт. Оно дано от природы и, таким образом, существует вне всякого вытеснения и личного опыта. Это понятие будет иметь большое значение как для теории, так и для клинической практики.',
        'Как бы то ни было, Юнг дополняет работы Фрейда выявлением синхронии* и того, что он называет «коллективным бессознательным». Согласно Юнгу, это «работающее» в нас коллективное бессознательное, передаваемое в обществе от поколения к поколению, аккумулирует человеческий опыт. Оно дано от природы и, таким образом, существует вне всякого вытеснения и личного опыта. Это понятие будет иметь большое значение как для теории, так и для клинической практики.',
        'Анн Анселин Шутценбергер',
        'Синдром предков. Трансгенерационные связи, семейные тайны, синдром годовщины, передача травм и практическое использование геносоциограммы',
        null, -1339682333, '2023-11-11 07:47:59.486046', '2023-11-11 07:47:59.486047', 'UNREAD', 50);
INSERT INTO public.quotes (id, text, original_text, author, book_name, description, hash, created_at, updated_at,
                           status, priority)
VALUES ('bd4074f9-ddba-46f9-8860-470ad6cc2312',
        'Мы отрицаем дар понимания вещей, который даётся нам нашими чувствами. В результате теоретическое осмысление процесса восприятия отделилось от самого восприятия, и наша мысль движется в абстракции. Наши глаза превратились в простой инструмент измерения и опознавания — отсюда недостаток идей, которые могут быть выражены в образах, а также неумение понять смысл того, что мы видим. Естественно, что в этой ситуации мы чувствуем себя потерянными среди предметов, которые предназначены для непосредственного восприятия. Поэтому мы обращаемся к более испытанному средству — к помощи слов.',
        'МЫ ОТРИЦАЕМ ДАР ПОНИМАНИЯ ВЕЩЕЙ, КОТОРЫЙ ДАЕТСЯ НАМ НАШИМИ  ЧУВСТВАМИ. В РЕЗУЛЬТАТЕ ТЕОРЕТИЧЕСКОЕ ОСМЫСЛЕНИЕ ПРОЦЕССА ВОСПРИЯТИЯ  ОТДЕЛИЛОСЬ ОТ САМОГО ВОСПРИЯТИЯ И НАША МЫСЛЬ ДВИЖЕТСЯ В АБСТРАКЦИИ. НАШИ ГЛАЗА  ПРЕВРАТИЛИСЬ В ПРОСТОЙ ИНСТРУМЕНТ ИЗМЕРЕНИЯ И ОПОЗНАВАНИЯ— ОТСЮДА  НЕДОСТАТОК ИДЕЙ, КОТОРЫЕ МОГУТ БЫТЬ ВЫРАЖЕНЫ В ОБРАЗАХ, А ТАКЖЕ НЕУМЕНИЕ  ПОНЯТЬ СМЫСЛ ТОГО, ЧТО МЫ ВИДИМ. ЕСТЕСТВЕННО, ЧТО В ЭТОЙ СИТУАЦИИ МЫ ЧУВСТВУЕМ  СЕБЯ ПОТЕРЯННЫМИ СРЕДИ ПРЕДМЕТОВ, КОТОРЫЕ ПРЕДНАЗНАЧЕНЫ ДЛЯ НЕ-  ПОСРЕДСТВЕННОГО ВОСПРИЯТИЯ. ПОЭТОМУ МЫ ОБРАЩАЕМСЯ К БОЛЕЕ ИСПЫТАННОМУ  СРЕДСТВУ — К ПОМОЩИ СЛОВ.',
        'Рудольф Арнхейм', 'Искусство и визуальное восприятие', null, 1299518801, '2023-11-11 07:46:28.576143',
        '2023-11-11 07:46:28.576152', 'UNREAD', 50);
